DROP TABLE IF EXISTS categoria;

CREATE TABLE categoria (
    "id" BIGINT GENERATED BY DEFAULT AS IDENTITY,
    "titulo" VARCHAR(100) NOT NULL,
    "cor" VARCHAR(7) NOT NULL,
    PRIMARY KEY ("id")
);

