    );

ALTER TABLE video
ADD "categoria_id" BIGINT;

ALTER TABLE video
ADD CONSTRAINT FK_categoria_id
FOREIGN KEY (categoria_id) REFERENCES categoria(id);

UPDATE video
SET categoria_id = 1;